import React, { useState, useEffect } from "react";
import logo from './logo.svg';
import './App.css';
import { useParams } from "react-router-dom";
import Main from "./components/Main"
import {getUser} from "./api/UserAPI";

function App() {
  // Hook used to access path paramaters
  const urlParams = useParams();
  const userId = urlParams.userId
  const [dataLoaded, setDataLoaded] = useState(false);
  const [userData, setUserData] = useState([]);
  // Logging
  console.log("URL PARAMS: " + JSON.stringify(urlParams));
  console.log("USER ID: " + userId);



  // Retrieve user information from API on component load
  useEffect(() => {
    console.log("[APP] LOADING USER DATA: " + dataLoaded);
    getUser(userId).then((response) => {
      //const userData = response
      const user_data = JSON.stringify(response)
      setUserData(user_data);
      setDataLoaded(true);
      console.log("USER DATA: " + userData.first_name);
    });
  }, []);


  return (
    <div className="App">
      <header className="App-header">
        <img src={userData.avatar} className="App-logo" alt="logo" />
        <p>User ID: {userId}</p>
        <p>User Data: {userData}</p>
      </header>
    </div>
  );
}

export default App;
