import React, { useState, useEffect } from "react";
import './App.css';
import { useParams } from "react-router-dom";
import UserPage from "./components/UserPage";
import {getUser} from "./api/UserAPI";

function App() {
  // Hook used to access path paramaters
  const urlParams = useParams();
  const userId = urlParams.userId;
  const [dataLoaded, setDataLoaded] = useState(false);
  const [userData, setUserData] = useState([]);
  // Debug / Logging
  console.log("URL PARAMS: " + JSON.stringify(urlParams));
  console.log("USER ID: " + userId);

  // Retrieve user information from API on component load
  useEffect(() => {
    console.log("[APP] LOADING USER DATA: " + dataLoaded);
    getUser(userId).then((response) => {
      setUserData(response);
      setDataLoaded(true);
      console.log("USER DATA: " + JSON.stringify(response));
    });
  }, []);

  return (
    <div>
      <header className="App-header">
        <div>Full-Stack Engineer: Line-Up Coding Exercise - David Okeke</div>
      </header>
      <div className="UserPage">
        {/*Load UserPage component once data has been retrieved*/}
        {dataLoaded && <UserPage userData={userData} />}
      </div>
    </div>
  );
}

export default App;
