import React, { useState, useEffect } from "react";
import logo from './logo.svg';
import './App.css';
import { useParams } from "react-router-dom";
import Main from "./components/Main"
import {getUser} from "./api/UserAPI";

function DisplayUser(props) {
  const isDisplayed = true;
  const userData = props.userData;
  return (
    <div className="App">
      <header className="App-header">
        <img src={userData.avatar} alt="logo" />
        <p>
          <div>ID: {userData.id}</div>
          <div>Email: {userData.email}</div>
          <div>First Name: {userData.first_name}</div>
          <div>Last Name: {userData.last_name}</div>
        </p>
      </header>
    </div>
  );
}


function App() {
  // Hook used to access path paramaters
  const urlParams = useParams();
  const userId = urlParams.userId
  const [dataLoaded, setDataLoaded] = useState(false);
  const [userData, setUserData] = useState([]);
  // Debug / Logging
  console.log("URL PARAMS: " + JSON.stringify(urlParams));
  console.log("USER ID: " + userId);

  // Retrieve user information from API on component load
  useEffect(() => {
    console.log("[APP] LOADING USER DATA: " + dataLoaded);
    getUser(userId).then((response) => {
      setUserData(response);
      setDataLoaded(true);
      console.log("USER DATA: " + JSON.stringify(response));
    });
  }, []);


  return (
    <div>
      {dataLoaded && <DisplayUser userData={userData}/>}
    </div>
  );
}

export default App;
